if (isset($oct_css_code[1]) && !empty($oct_css_code[1]) && !defined('HTTP_CATALOG')) {
	$oct_output = preg_replace("/<style>.*?<\/style>/is", '', $oct_output);

	$styles = "<style>";

	foreach ($oct_css_code[1] as $code) {
